import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./styles.css"

export const Home = () => {
    const [name, setName] = React.useState("");
    const [age, setAge] = React.useState(0);
    const [study, setStudy] = React.useState("");
    const [gender, setGender] = React.useState("");
    return (
        <>
            <Link style={{ fontSize: "30px", color: "orange" }} to="/data">Data</Link>
            <form>
                <div class="form-outline mb-4">
                    <label style={{ fontSize: "20px", color: "green", marginTop: "20px" }} class="form-label" htmlFor="name">Name
                        <input class="form-control" type="text" id="name" name="name" value={
                            name
                        } onChange={(e) => {
                            setName(e.target.value);
                        }} />
                    </label>
                </div>
                <div class="form-outline mb-4">
                    <input type="radio" id="html" name="gender" value="MALE" onChange={
                        (e) => {
                            setGender(e.target.value);
                        }
                    } />
                    <label style={{ fontSize: "20px", color: "green" }} htmlFor="male">MALE</label>
                    <input type="radio" id="female" name="gender" value="FEMALE" onChange={
                        (e) => {
                            setGender(e.target.value);
                        }
                    } />
                    <label style={{ fontSize: "20px", color: "green" }} htmlFor="female">FEMALE</label>
                    <input type="radio" id="transgender" name="gender" value="TRANSGENDER" onChange={
                        (e) => {
                            setGender(e.target.value);
                        }
                    } />
                    <label style={{ fontSize: "20px", color: "green" }} htmlFor="transgender">TRANSGENDER</label>
                </div>
                <div class="form-outline mb-4">
                    <label style={{ fontSize: "20px", color: "green" }} class="form-label" htmlFor="age">Age
                        <input class="form-control" type="number" id="age" name="age" value={age} onChange={(e) => {
                            setAge(e.target.value);
                        }} />
                    </label>
                </div>
                <div class="form-outline mb-4">
                    <label style={{ fontSize: "20px", color: "green" }} class="form-label" htmlFor="study">highest study
                        <input class="form-control" type="text" id="study" name="study" value={study} onChange={
                            (e) => {
                                setStudy(e.target.value);
                            }
                        } />
                    </label>
                </div>

                <button class="button-88" type="submit" onClick={(e) => {
                    e.preventDefault();
                    const data = {
                        timestamp: new Date(),
                        name,
                        age,
                        study,
                        gender
                    }
                    axios.post("http://localhost:3001/data", data).then((res) => {
                        setAge(0);
                        setName("");
                        setStudy("");
                        setGender("");
                        alert("Data saved successfully");
                    }).catch((err) => {
                        console.log(err);
                    })
                }}>Submit</button>
            </form>
        </>
    );
}
