import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./styles.css"

export const Home = () => {
    const [name, setName] = React.useState("");
    const [age, setAge] = React.useState(0);
    const [study, setStudy] = React.useState("");
    const [blog, setblog] = React.useState(0);
    return (
        <>
            <Link style={{ fontSize: "30px", color: "orange" }} to="/data">Users</Link>

            <div class="login-box">
                <h2>Info</h2>
                <form>
                    <div class="user-box">
                        <input type="text" id="name" name="name" value={
                            name
                        } onChange={(e) => {
                            setName(e.target.value);
                        }} />
                        <label htmlFor="name">Name
                        </label>
                    </div>
                    <div class="user-box">
                        <input type="number" id="blog" name="blog" value={blog} onChange={(e) => {
                            setblog(e.target.value);
                        }} />
                        <label htmlFor="blog">Blog number
                        </label>
                    </div>

                    <div class="user-box">
                        <input type="number" id="age" name="age" value={age} onChange={(e) => {
                            setAge(e.target.value);
                        }} />
                        <label htmlFor="age">Age

                        </label>
                    </div>
                    <div class="user-box">
                        <input type="textarea" id="study" name="study" value={study} onChange={
                            (e) => {
                                setStudy(e.target.value);
                            }
                        } />
                        <label htmlFor="study">Blog

                        </label>
                    </div>

                    <button class="button-88" type="submit" onClick={(e) => {
                        e.preventDefault();
                        const data = {
                            name,
                            age,
                            study,
                            blog
                        }
                        axios.post("http://localhost:3001/data", data).then((res) => {
                            setAge(0);
                            setName("");
                            setStudy("");
                            setblog(0);
                            alert("Saved data");
                        }).catch((err) => {
                            console.log(err);
                        })
                    }}>Submit</button>
                </form>
            </div>
        </>
    );
}
