import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./styles.css"

export const Home = () => {
    const [name, setName] = React.useState("");
    const [roll, setroll] = React.useState("");
    const [time, settime] = React.useState("")
    return (
        <>
            <Link style={{ fontSize: "30px", color: "orange" }} to="/data">Users</Link>
            <div class="container">
                <div class="brand-logo"></div>
                <div class="brand-title">Class room</div>
                <form>
                    <div class="inputs">
                        <label> Name</label>
                        <input type="text" id="name" name="name" placeholder="example:Yash" value={
                            name
                        } onChange={(e) => {
                            setName(e.target.value);
                        }} />
                        <label>Roll no</label>
                        <input type="text" id="roll" name="roll" value={roll} placeholder="S20200010***" onChange={
                            (e) => {

                                setroll(e.target.value);
                            }
                        } />
                        <input type="text" id="time" name="time" value={time} placeholder="S20200010***" onChange={
                            (e) => {
                                const date = new Date();
                                const d = date.getHours();
                                const m = date.getMinutes();
                                settime(d + m);
                                // settime(e.target.value);
                            }
                        } />
                        <button class="button-88" type="submit" onClick={(e) => {
                            e.preventDefault();
                            const data = {
                                name,
                                roll,
                                time
                            }
                            axios.post("http://localhost:3001/data", data).then((res) => {

                                setName("");

                                setroll("");
                                alert("Saved data");
                            }).catch((err) => {
                                console.log(err);
                            })
                        }}>Submit</button>

                    </div>
                </form>

            </div>








        </>
    );
}
