import { Link } from "react-router-dom";
import React, { useEffect } from "react";
import "./styles.css"

export const Blogs = () => {
    const [data, setData] = React.useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch("http://localhost:3001/data");
            const data = await response.json();
            setData(data);
        }
        fetchData();
    }, []);

    const blogfilter = (e) => {
        let d = data.filter(item => item.roll.includes((e.target.value)))
        setData(d);
    }
    return (
        <>
            <div>
                <Link style={{ fontSize: "30px", color: "orange" }} to="/">Home</Link>
                <Link style={{ fontSize: "30px", color: "orange" }} to="/data">Users</Link>
                <Link style={{ fontSize: "30px", color: "orange" }} to="/blog">Users</Link>
                <div >
                    <label>User number</label>
                    <input onChange={blogfilter} class="user-box" type="number" />
                    <div>
                        {
                            data.map((item, i) => {
                                return (
                                    <div >
                                        <h3>Blog {i}</h3>
                                        <h5>{item.study}</h5>
                                        <h6 style={{ color: "green" }}>written by {item.name} id {item.roll}</h6>
                                        <div>-----------------------------------------------------------------------------</div>

                                    </div>
                                )
                            })
                        }
                    </div>
                </div>


            </div>

        </>
    )

}
